import React, { useState, useEffect } from "react";
import RadioButtons from "./Radiobuttons";
import Tabs from "./Tabs";

const ART_CATEGORIES = [
  {
    category: "audio",
    choices: ["acoustic", "cinematic", "rock"]
  },
  {
    category: "image",
    choices: ["butterfly", "flower", "planet"]
  },
  {
    category: "text",
    choices: ["lyric", "poem", "quote"]
  }
];

function App() {
  //Keeps track of active choices
  const [activeAudio, setActiveAudio] = useState("acoustic");
  const [activeImage, setActiveImage] = useState("butterfly");
  const [activeText, setActiveText] = useState("lyric");
  const [activeTab, setActiveTab] = useState("1");

  //Arrays
  const [texts, setTexts] = useState([null, null, null, null]);
  const [images, setImages] = useState([null, null, null, null]);
  const [audios, setAudios] = useState([null, null, null, null]);

  function changeActiveArt(category, active) {
    switch (category) {
      case "audio":
        setActiveAudio(active);
        break;
      case "image":
        setActiveImage(active);
        break;
      case "text":
        setActiveText(active);
        break;
      default:
        break;
    }
  }

  useEffect(() => {
    if (texts[activeTab] === null) {
      console.log("Fetching text");
      fetch("/content/text/" + activeText + ".json")
        .then(res => res.json())
        .then(res => {
          setTexts(arr =>
            arr.map((item, index) => {
              console.log(index, activeTab);
              return index === activeTab ? "new" : "old";
              //return index === activeTab ? res.items[activeTab - 1] : item;
            })
          );
        });
    }
  }, [activeText, activeTab]);

  useEffect(() => {
    if (images[activeTab] === null) {
      fetch(
        "/content/image/" + activeImage + "/" + activeImage + activeTab + ".svg"
      )
        .then(res => res.text())
        .then(svg => {
          setImages(arr =>
            arr.map((item, index) => {
              return index === activeTab ? svg : item;
            })
          );
        });
    }
  }, [activeImage, activeTab]);

  useEffect(() => {
    if (audios[activeTab] === null) {
      setAudios(arr =>
        arr.map((item, index) => {
          return index === activeTab
            ? "/content/audio/" +
                activeAudio +
                "/" +
                activeAudio +
                activeTab +
                ".mp3"
            : item;
        })
      );
    }
  }, [activeAudio, activeTab]);

  function changeActiveTab(active) {
    setActiveTab(active);
  }

  useEffect(() => {
    console.log(activeAudio, activeImage, activeText, activeTab);
    console.log(texts);
  }, [activeAudio, activeImage, activeText, activeTab]);

  return (
    <div className="App">
      <div className="grid-container">
        <div className="header">
          <h1>Art Exhibition</h1>
        </div>
        <div>
          <Tabs changeActiveTab={changeActiveTab} />
        </div>
        <div className="grid-container-art">
          <div
            dangerouslySetInnerHTML={{ __html: images[activeTab] }}
            className="grid-item-image"
          ></div>
          <div className="grid-item-text">
            {texts[activeTab] !== null &&
              texts[activeTab].split("\n").map((i, key) => {
                return <div key={key}>{i}</div>;
              })}
          </div>
          <div className="grid-item-text">
            <audio src={audios[activeTab]} type="audio/mp3" controls></audio>
          </div>
        </div>
        <div>
          <div className="flex-container-radio">
            {ART_CATEGORIES.map((element, index) => {
              return (
                <RadioButtons
                  changeActiveArt={changeActiveArt}
                  key={index}
                  category={element.category}
                  choices={element.choices}
                />
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
