import React, { useState, useEffect } from "react";
import RadioButtons from "./Radiobuttons";
import Tabs from "./Tabs";

const ART_CATEGORIES = [
  {
    category: "audio",
    choices: ["acoustic", "cinematic", "rock"]
  },
  {
    category: "image",
    choices: ["butterfly", "flower", "planet"]
  },
  {
    category: "text",
    choices: ["lyric", "poem", "quote"]
  }
];

function App() {
  //Keeps track of active categories:
  const [activeAudioCategory, setActiveAudioCategory] = useState("rock");
  const [activeImageCategory, setActiveImageCategory] = useState("flower");
  const [activeTextCategory, setActiveTextCategory] = useState("lyric");

  const [activeTab, setActiveTab] = useState("1");
  const [texts, setTexts] = useState(null);
  const [image, setImage] = useState(null);
  const [audio, setAudio] = useState(null);

  function changeActiveArt(category, active) {
    switch (category) {
      case "audio":
        setActiveAudioCategory(active);
        break;
      case "image":
        setActiveImage(active);
        break;
      case "text":
        setActiveText(active);
        break;
      default:
        break;
    }
  }

  useEffect(() => {
    setTexts(null);
    fetch("/content/text/" + activeText + ".json")
      .then(res => res.json())
      .then(res => setTexts(res.items[activeTab - 1]));
  }, [activeText, activeTab]);

  useEffect(() => {
    fetchAudio();
  }, [activeAudio, activeTab]);

  useEffect(() => {
    setImage(null);
    fetch(
      "/content/image/" + activeImage + "/" + activeImage + activeTab + ".svg"
    )
      .then(res => res.text())
      .then(svg => {
        setImage(svg);
      });
  }, [activeImage, activeTab]);

  function changeActiveTab(active) {
    setActiveTab(active);
  }

  function fetchAudio(){
    setAudio("/content/audio/" + activeAudio + "/" + activeAudio + activeTab+ ".mp3");
    console.log("/content/audio/" + activeAudio + "/" + activeAudio + activeTab+ ".mp3");
  }

  useEffect(
    () => console.log(activeAudio, activeImage, activeText, activeTab),
    [activeAudio, activeImage, activeText, activeTab]
  );

  return (
    <div className="App">
      <div className="grid-container">
        <div className="header">
          <h1>Art Exhibition</h1>
        </div>
        <div>
          <Tabs changeActiveTab={changeActiveTab} />
        </div>
        <div className="grid-container-art">
          <div
            dangerouslySetInnerHTML={{ __html: image }}
            className="grid-item-image"
          ></div>
          <div className="grid-item-text">
            {texts !== null &&
              texts.split("\n").map((i, key) => {
                return <div key={key}>{i}</div>;
              })}
          </div>
          <div className="grid-item-text"><audio src = {audio} type = "audio/mp3" controls>
          </audio></div>
        </div>
        <div>
          <div className="flex-container-radio">
            {ART_CATEGORIES.map((element, index) => {
              return (
                <RadioButtons
                  changeActiveArt={changeActiveArt}
                  key={index}
                  category={element.category}
                  choices={element.choices}
                />
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
