import React, { useState, useEffect } from "react";
import RadioButtons from "./Radiobuttons";
import Tabs from "./Tabs";

const ART_CATEGORIES = [
  {
    category: "audio",
    choices: ["acoustic", "cinematic", "rock"]
  },
  {
    category: "image",
    choices: ["butterfly", "flower", "painting"]
  },
  {
    category: "text",
    choices: ["lyric", "poem", "quote"]
  }
];

function App() {
  const [activeAudio, setActiveAudio] = useState("rock");
  const [activeImage, setActiveImage] = useState("flower");
  const [activeText, setActiveText] = useState("painting");
  const [activeTab, setActiveTab] = useState("1");
  const [texts, setTexts] = useState(null);
  const [image, setImage] = useState(null);

  function changeActiveArt(category, active) {
    switch (category) {
      case "audio":
        setActiveAudio(active);
        break;
      case "image":
        setActiveImage(active);
        break;
      case "text":
        setActiveText(active);
        break;
      default:
        break;
    }
  }

  useEffect(() => {
    fetchText();
  }, [activeText, activeTab]);

  useEffect(() => {
    //fetchAudio();
  }, [activeAudio, activeTab]);

  useEffect(() => {
    fetchImage();
  }, [activeImage, activeTab]);

  function changeActiveTab(active) {
    setActiveTab(active);
  }

  function fetchText() {
    console.log("fetch text");
    fetch("/content/text/" + activeText + ".json")
      .then(res => res.json())
      .then(res => setTexts(res.items[activeTab - 1]));
  }

  function fetchImage() {
    console.log(
      "fetch image: /content/image/" +
        activeImage +
        "/" +
        activeImage +
        activeTab +
        ".svg"
    );
    fetch(
      "/content/image/" + activeImage + "/" + activeImage + activeTab + ".svg"
    )
      .then(res => res.text())
      .then(svg => {
        setImage(svg);
      });
  }

  useEffect(
    () => console.log(activeAudio, activeImage, activeText, activeTab),
    [activeAudio, activeImage, activeText, activeTab]
  );

  return (
    <div className="App">
      <div className="grid-container">
        <div className="grid-item"></div>
        <div className="grid-item">
          <Tabs changeActiveTab={changeActiveTab} />
        </div>
        <div>{texts !== null && texts}</div>
        <div dangerouslySetInnerHTML >}</div>
        <div className="grid-item">
          <div className="flex-container-radio">
            {ART_CATEGORIES.map((element, index) => {
              return (
                <RadioButtons
                  changeActiveArt={changeActiveArt}
                  key={index}
                  category={element.category}
                  choices={element.choices}
                />
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
