import React, { useState, useEffect } from "react";
import RadioButtons from "./Radiobuttons";
import Tabs from "./Tabs";

const ART_CATEGORIES = [
  {
    category: "audio",
    choices: ["acoustic", "cinematic", "rock"]
  },
  {
    category: "image",
    choices: ["butterfly", "flower", "planet"]
  },
  {
    category: "text",
    choices: ["lyric", "poem", "quote"]
  }
];

function App() {
  //Keeps track of active categories:
  const [activeAudioCategory, setActiveAudioCategory] = useState("rock");
  const [activeImageCategory, setActiveImageCategory] = useState("flower");
  const [activeTextCategory, setActiveTextCategory] = useState("lyric");

  //Stores media being displayed:
  const [activeTab, setActiveTab] = useState("1");
  const [text, setText] = useState(null);
  const [image, setImage] = useState(null);
  const [audio, setAudio] = useState(null);

  function changeActiveArt(category, active) {
    switch (category) {
      case "audio":
        setActiveAudioCategory(active);
        break;
      case "image":
        setActiveImageCategory(active);
        break;
      case "text":
        setActiveTextCategory(active);
        break;
      default:
        break;
    }
  }

  function changeActiveTab(active) {
    setActiveTab(active);
  }

  useEffect(() => {
    // Displays nothing while fetching new text to enhance UX
    setText(null);
    fetch("/content/text/" + activeTextCategory + ".json")
      .then(res => res.json())
      .then(res => setText(res.items[activeTab - 1]));
  }, [activeTextCategory, activeTab]);

  useEffect(() => {
    // Displays nothing while fetching new audio to enhance UX
    setAudio(null);
    setAudio(
      "/content/audio/" +
        activeAudioCategory +
        "/" +
        activeAudioCategory +
        activeTab +
        ".mp3"
    );
  }, [activeAudioCategory, activeTab]);

  useEffect(() => {
    // Displays nothing while fetching new image to enhance UX
    setImage(null);
    fetch(
      "/content/image/" +
        activeImageCategory +
        "/" +
        activeImageCategory +
        activeTab +
        ".svg"
    )
      .then(res => res.text())
      .then(svg => {
        setImage(svg);
      });
  }, [activeImageCategory, activeTab]);

  useEffect(
    () =>
      console.log(
        activeAudioCategory,
        activeImageCategory,
        activeTextCategory,
        activeTab
      ),
    [activeAudioCategory, activeImageCategory, activeTextCategory, activeTab]
  );

  return (
    <div className="App">
      <div className="grid-container">
        <div className="header">
          <h1>Art Exhibition</h1>
        </div>
        <div>
          <Tabs changeActiveTab={changeActiveTab} />
        </div>
        <div className="grid-container-art">
          <div
            dangerouslySetInnerHTML={{ __html: image }}
            className="grid-item-image"
          ></div>
          <div className="grid-item-text">
            {text !== null &&
              text.split("\n").map((i, key) => {
                return <div key={key}>{i}</div>;
              })}
          </div>
          <div className="grid-item-text">
            <audio src={audio} type="audio/mp3" controls></audio>
          </div>
        </div>
        <div>
          <div className="flex-container-radio">
            {ART_CATEGORIES.map((element, index) => {
              return (
                <RadioButtons
                  changeActiveArt={changeActiveArt}
                  key={index}
                  category={element.category}
                  choices={element.choices}
                />
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
