import React, { useState, useEffect } from "react";
import RadioButtons from "./Radiobuttons";
import Tabs from "./Tabs";

const ART_CATEGORIES = [
  {
    category: "audio",
    choices: ["acoustic", "cinematic", "rock"]
  },
  {
    category: "image",
    choices: ["butterfly", "flower", "planet"]
  },
  {
    category: "text",
    choices: ["lyric", "poem", "quote"]
  }
];

export default function App() {
  //Keeps track of active categories:
  const [activeAudioCategory, setActiveAudioCategory] = useState("acoustic");
  const [activeImageCategory, setActiveImageCategory] = useState("butterfly");
  const [activeTextCategory, setActiveTextCategory] = useState("lyric");

  //Stores media being displayed:
  const [activeTab, setActiveTab] = useState("0");
  const [text, setText] = useState(null);
  const [image, setImage] = useState(null);
  const [audio, setAudio] = useState(null);

  function changeActiveArt(category, active) {
    switch (category) {
      case "audio":
        setActiveAudioCategory(active);
        break;
      case "image":
        setActiveImageCategory(active);
        break;
      case "text":
        setActiveTextCategory(active);
        break;
      default:
        break;
    }
  }

  function changeActiveTab(active) {
    setActiveTab(active);
  }

  //Fetch texts
  useEffect(() => {
    // Displays nothing while fetching new text to enhance UX
    setText(null);
    const url = "/content/text/" + activeTextCategory + ".json";
    let data = sessionStorage.getItem(url);
    if (data) {
      setText(JSON.parse(data).items[activeTab]);
    } else {
      fetch(url)
        .then(res => res.json())
        .then(res => {
          setText(res.items[activeTab]);
          sessionStorage.setItem(url, JSON.stringify(res));
        });
    }
  }, [activeTextCategory, activeTab]);

  //Fetch audio
  useEffect(() => {
    // Displays nothing while fetching new audio to enhance UX
    setAudio(null);

    const url = `/content/audio/${activeAudioCategory}/${activeAudioCategory}${activeTab}.mp3`;
    let data = sessionStorage.getItem(url);

    if (data) {
      setAudio(JSON.parse(data));
    } else {
      setAudio(url);
      sessionStorage.setItem(url, JSON.stringify(url));
    }
  }, [activeAudioCategory, activeTab]);

  //Fetch image
  useEffect(() => {
    // Displays nothing while fetching new image to enhance UX
    setImage(null);
    const url = `/content/image/${activeImageCategory}/${activeImageCategory}${activeTab}.svg`;

    let data = sessionStorage.getItem(url);
    if (data) {
      setImage(JSON.parse(data));
    } else {
      fetch(url)
        .then(res => res.text())
        .then(svg => {
          setImage(svg);
          sessionStorage.setItem(url, JSON.stringify(svg));
        });
    }
  }, [activeImageCategory, activeTab]);

  useEffect(
    () =>
      console.log(
        activeAudioCategory,
        activeImageCategory,
        activeTextCategory,
        activeTab
      ),
    [activeAudioCategory, activeImageCategory, activeTextCategory, activeTab]
  );

  function makeFavourite() {
    if (typeof Storage !== "undefined") {
      localStorage.favourite = {
        image: activeImageCategory,
        audio: activeAudioCategory,
        text: activeTextCategory,
        tab: activeTab
      };
    }
  }

  function clickCounter() {
    if (typeof Storage !== "undefined") {
      if (localStorage.clickcount) {
        localStorage.clickcount = Number(localStorage.clickcount) + 1;
      } else {
        localStorage.clickcount = 1;
      }
    }
  }

  function showFavourite(){
    if (typeof Storage !== "undefined") {
      if (localStorage.favourite) {
        setActiveAudioCategory
      } else {
        localStorage.clickcount = 1;
      }
    }
  }

  return (
    <div className="App" onClick={clickCounter}>
      <div className="grid-container">
        <div className="header">
          <h1>Art Exhibition</h1>
          <h4>Num clicks: {localStorage.clickcount}</h4>
        </div>
        <div>
          <Tabs changeActiveTab={changeActiveTab} />
        </div>
        <div className="grid-container-art">
          <div
            dangerouslySetInnerHTML={{ __html: image }}
            className="grid-item-image"
          ></div>
          <div className="grid-item-text">
            {text !== null &&
              text.split("\n").map((i, key) => {
                return <div key={key}>{i}</div>;
              })}
          </div>
          <div className="grid-item-text">
            <audio src={audio} type="audio/mp3" controls></audio>
          </div>
        </div>
        <div>
          <button onClick={makeFavourite}>Make favourite</button>
          <button onClick={showFavourite}>Show favourite</button>
          <div className="flex-container-radio menu">
            {ART_CATEGORIES.map((element, index) => {
              return (
                <RadioButtons
                  changeActiveArt={changeActiveArt}
                  key={index}
                  category={element.category}
                  choices={element.choices}
                />
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}
