import React, { useState, useEffect } from "react";
import RadioButtons from "./Radiobuttons";
import Tabs from "./Tabs";
import ajax from "axios";

const ART_CATEGORIES = [
  {
    category: "audio",
    choices: ["acoustic", "cinematic", "rock"]
  },
  {
    category: "image",
    choices: ["butterfly", "flower", "painting"]
  },
  {
    category: "text",
    choices: ["lyric", "poem", "quote"]
  }
];

const CONTENT_PATH = "./../../public/content";

function App() {
  const [activeAudio, setAudio] = useState("rock");
  const [activeImage, setImage] = useState("flower");
  const [activeText, setText] = useState("painting");
  const [activeTab, setTab] = useState("1");

  function changeActiveArt(category, active) {
    switch (category) {
      case "audio":
        setAudio(active);
        break;
      case "image":
        setImage(active);
        break;
      case "text":
        setText(active);
        fetchText(active);
        break;
      default:
        break;
    }
  }

  function changeActiveTab(active) {
    setTab(active);
  }

  function fetchText(active) {
    console.log(CONTENT_PATH + "/text/" + active + ".json");
    axios.fetch(CONTENT_PATH + "/text/" + active + ".json").then(res => {
      console.log(res);
    });
  }

  useEffect(
    () => console.log(activeAudio, activeImage, activeText, activeTab),
    [activeAudio, activeImage, activeText, activeTab]
  );

  return (
    <div className="App">
      <div className="grid-container">
        <div className="grid-item"></div>
        <div className="grid-item">
          <Tabs changeActiveTab={changeActiveTab} />
        </div>
        <div className="grid-item">
          <div className="flex-container-radio">
            {ART_CATEGORIES.map((element, index) => {
              return (
                <RadioButtons
                  changeActiveArt={changeActiveArt}
                  key={index}
                  category={element.category}
                  choices={element.choices}
                />
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
