import React from "react";
import RadioButtons from "./Radiobuttons";

const ART_CATEGORIES = [
  {
    category: "audio",
    choices: ["acoustic", "cinematic", "rock"]
  },
  {
    category: "images",
    choices: ["butterfly", "flower", "painting"]
  },
  {
    category: "text",
    choices: ["lyric", "poem", "quote"]
  }
];

function App() {

  const [activeAudio, setAudio]=useState(0);
  const [activeImage, setImage]=useState(0);
  const [activeText, setText]=useState(0);

  function changeActiveText(active){
    console.log(active)
    setText(active)
  }

  return (
    <div className="App">
      {ART_CATEGORIES.map((element, index) => {
        return (
          <RadioButtons
            changeActiveText={changeActiveText}
            key={index}
            category={element.category}
            choices={element.choices}
          />
        );
      })}
    </div>
  );
}

export default App;
