import React, { useState } from "react";

export default function RadioButtons() {
  function handleClick(e) {
    e.preventDefault();
    console.log(e.target.value);
  }

  return (
    <form>
      <div>
        <input
          type="radio"
          id="textChoice1"
          name="text"
          value="lyric"
          onClick={handleClick}
        />
        <label htmlFor="textChoice1">{props.choices[0]}</label>
        <input
          type="radio"
          id="textChoice2"
          name="text"
          value="poem"
          onClick={handleClick}
        />
        <label htmlFor="textChoice2">Poem</label>
        <input
          type="radio"
          id="textChoice3"
          name="text"
          value="quote"
          onClick={handleClick}
        />
        <label htmlFor="textChoice3">Quote</label>
      </div>
    </form>
  );
}
