import React, { useState } from "react";

export default function RadioButtons(props) {
  function handleClick(e) {
    props.changeActiveText(e.target.value);
  }

  return (
    <form>
      <h1>{props.category}</h1>
      {props.choices.map((element, index) => {
        return (
          <div>
            <input
              key={index}
              type="radio"
              name={props.category}
              value={element}
              onClick={handleClick}
            />
            <label htmlFor="textChoice1">{element}</label>
          </div>
        );
      })}
    </form>
  );
}
